mapper.select:
select * from (select rownum no, tv_no,tv_teamid, tv_title, (select t_name from team where t_id = tv_teamid) tv_tname, tv_date, tv_count, tvc_no, tvc_path, cr_path, default_path from (select rownum no, tv_no,tv_teamid, tv_title, tv_date, tv_count, tvc_no, tvc_path, cr_path, nvl2(tvc_path,null,nvl2(cr_path,null,'images/default/'||ceil(DBMS_RANDOM.VALUE(1,10))||'.jpg')) default_path from TRIPREVIEW_MAIN_VIEW where (tv_no, nvl(tvc_no,0)) in (select tv_no, max(nvl(tvc_no,0)) from TRIPREVIEW_MAIN_VIEW where tvc_path is not null or cr_path is not null group by tv_no union select tv_no, max(nvl(tvc_no,0)) from TRIPREVIEW_MAIN_VIEW where tvc_path is null and cr_path is null and tv_no not in (select tv_no from TRIPREVIEW_MAIN_VIEW where tvc_path is not null or cr_path is not null group by tv_no) group by tv_no) and tv_title like #{keyword} order by
( case when tv_title like '%'||(select m_search from (select m_search, rownum no from (select m_search, count(m_search) count from keysearch where m_id = #{m_id} group by m_search order by count desc)) where no = 1)||'%' then tv_title end ) asc, ( case when tv_title like '%'||(select m_search from (select m_search, rownum no from (select m_search, count(m_search) count from keysearch where m_id = #{m_id} group by m_search order by count desc)) where no = 2)||'%' then tv_title end ) asc, ( case when tv_title like '%'||(select m_search from (select m_search, rownum no from (select m_search, count(m_search) count from keysearch where m_id = #{m_id} group by m_search order by count desc)) where no = 3)||'%' then tv_title end ) asc, ( case when tv_title like '%'||(select m_search from (select m_search, rownum no from (select m_search, count(m_search) count from keysearch where m_id = #{m_id} group by m_search order by count desc)) where no = 4)||'%' then tv_title end ) asc, ( case when tv_title like '%'||(select m_search from (select m_search, rownum no from (select m_search, count(m_search) count from keysearch where m_id = #{m_id} group by m_search order by count desc)) where no = 5)||'%' then tv_title end ) asc,
tv_no desc)) where no between #{start} and #{end}
